// 对话领域卡片交互占位脚本
const cardsContainer = document.getElementById('cards-container');
const fieldBtns = document.querySelectorAll('.field-btn');
const demoCards = {
  craft: [
    {img: '卡片图片', name: '工艺项目A'},
    {img: '卡片图片', name: '工艺项目B'}
  ],
  medicine: [
    {img: '卡片图片', name: '医药项目A'},
    {img: '卡片图片', name: '医药项目B'}
  ],
  other: [
    {img: '卡片图片', name: '其他项目A'}
  ]
};
function renderCards(field) {
  cardsContainer.innerHTML = '';
  demoCards[field].forEach(card => {
    const div = document.createElement('div');
    div.className = 'card';
    div.innerHTML = `<div class='card-img'>${card.img}</div><div>${card.name}</div>`;
    div.onclick = () => window.location.href = 'project.html';
    cardsContainer.appendChild(div);
  });
}
fieldBtns.forEach(btn => {
  btn.onclick = () => renderCards(btn.dataset.field);
});
// 默认显示第一个领域
renderCards('craft');

// 论坛提问+回答占位脚本
const forumList = document.getElementById('forum-list');
const askBtn = document.getElementById('ask-btn');
const questionInput = document.getElementById('forum-question');
const demoAnswers = [
  {q: '传统工艺与现代科技如何结合？', a: '可以通过现代生物技术赋能传统工艺。'},
  {q: 'ICII全景地图如何参与？', a: '请联系组委会报名。'}
];
function renderForum() {
  forumList.innerHTML = '';
  demoAnswers.forEach(item => {
    const qDiv = document.createElement('div');
    qDiv.className = 'forum-question';
    qDiv.textContent = item.q;
    const aDiv = document.createElement('div');
    aDiv.className = 'forum-answer';
    aDiv.textContent = item.a;
    forumList.appendChild(qDiv);
    forumList.appendChild(aDiv);
  });
}
askBtn.onclick = function() {
  if(questionInput.value.trim()) {
    demoAnswers.unshift({q: questionInput.value, a: '（等待回答）'});
    questionInput.value = '';
    renderForum();
  }
};
renderForum();

// ICII全景地图交互占位脚本
// 实际地图和队伍数据需后续补充
const mapTooltip = document.getElementById('map-tooltip');
const mapSvg = document.querySelector('.map-svg');
if(mapSvg && mapTooltip) {
  mapSvg.addEventListener('mouseover', function(e) {
    mapTooltip.style.display = 'block';
    mapTooltip.textContent = '示例：江苏省 - NAU-China队伍';
  });
  mapSvg.addEventListener('mousemove', function(e) {
    mapTooltip.style.left = (e.pageX - mapSvg.offsetLeft + 20) + 'px';
    mapTooltip.style.top = (e.pageY - mapSvg.offsetTop + 20) + 'px';
  });
  mapSvg.addEventListener('mouseout', function() {
    mapTooltip.style.display = 'none';
  });
  mapSvg.addEventListener('click', function() {
    window.location.href = 'team.html';
  });
}
