// ICII全景中国地图ECharts实现（ECharts 6.x，需手动加载geoJSON）
window.onload = function() {
  var chartDom = document.getElementById('china-map');
  var myChart = echarts.init(chartDom);
  fetch('js/vendor/china.json')
    .then(res => res.json())
    .then(geoJson => {
      echarts.registerMap('china', geoJson);
      var option = {
        tooltip: {
          trigger: 'item',
          formatter: function(params) {
            return params.name;
          }
        },
        series: [
          {
            name: '中国',
            type: 'map',
            map: 'china',
            roam: true,
            itemStyle: {
              areaColor: '#3399ff', // 蓝色
              borderColor: '#fff',
              borderWidth: 1,
              emphasis: {
                areaColor: '#005fae', // hover时深蓝色
                shadowBlur: 10,
                shadowColor: 'rgba(0,0,0,0.3)'
              }
            },
            label: {
              show: true,
              color: '#fff',
              fontSize: 12
            },
            data: []
          }
        ]
      };
      myChart.setOption(option);
    });
};

// 中国地图geoJSON数据请从官方仓库下载后放置此处
// https://github.com/apache/echarts/blob/main/map/json/china.json
// 本文件仅为占位，实际部署时请用真实china.json替换
{
  "type": "FeatureCollection",
  "features": []
}
